import React from 'react';
import { Box, Flex, Button, Heading, Spacer, HStack, useToast } from '@chakra-ui/react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';

function Navbar() {
  const navigate = useNavigate();
  const toast = useToast();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isAuthenticated = !!localStorage.getItem('token');
  const isAdmin = user.role === 'admin';

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    toast({
      title: 'Logged out successfully',
      status: 'success',
      duration: 2000,
    });
    navigate('/login');
  };

  return (
    <Box bg="blue.500" px={4} py={3} position="sticky" top={0} zIndex={100}>
      <Flex maxW="container.xl" mx="auto" alignItems="center">
        <RouterLink to="/">
          <Heading size="md" color="white">Opinion Poll</Heading>
        </RouterLink>
        <Spacer />

        {isAuthenticated ? (
          <HStack spacing={4}>
            <RouterLink to="/create">
              <Button variant="ghost" color="white" _hover={{ bg: 'blue.600' }}>
                Create Poll
              </Button>
            </RouterLink>
            {isAdmin && (
              <RouterLink to="/admin">
                <Button variant="ghost" color="white" _hover={{ bg: 'blue.600' }}>
                  Admin
                </Button>
              </RouterLink>
            )}
            <Button
              onClick={handleLogout}
              colorScheme="whiteAlpha"
              variant="outline"
              _hover={{ bg: 'blue.600' }}
            >
              Logout
            </Button>
          </HStack>
        ) : (
          <HStack spacing={4}>
            <RouterLink to="/login">
              <Button variant="ghost" color="white" _hover={{ bg: 'blue.600' }}>
                Login
              </Button>
            </RouterLink>
            <RouterLink to="/signup">
              <Button colorScheme="whiteAlpha">Sign Up</Button>
            </RouterLink>
          </HStack>
        )}
      </Flex>
    </Box>
  );
}

export default Navbar;
