import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Heading,
  Card,
  CardBody,
  Stack,
  Radio,
  RadioGroup,
  Button,
  Text,
  Drawer,
  DrawerBody,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  useDisclosure,
  Input,
  VStack,
  Flex,
} from '@chakra-ui/react';

function Home() {
  const [polls, setPolls] = useState([
    {
      id: 1,
      question: 'What is your favorite programming language?',
      options: ['JavaScript', 'Python', 'Java', 'C++'],
      votes: [0, 0, 0, 0],
    },
  ]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [ws, setWs] = useState(null);

  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:3001');

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'chat') {
        setMessages(prev => [...prev, data.message]);
      } else if (data.type === 'vote') {
        updatePollVotes(data.pollId, data.optionIndex);
      }
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  const sendMessage = () => {
    if (newMessage.trim() && ws) {
      const messageData = {
        type: 'chat',
        message: { text: newMessage, timestamp: new Date().toISOString() }
      };
      ws.send(JSON.stringify(messageData));
      setMessages(prev => [...prev, messageData.message]);
      setNewMessage('');
    }
  };

  const submitVote = (pollId, optionIndex) => {
    if (ws) {
      ws.send(JSON.stringify({
        type: 'vote',
        pollId,
        optionIndex
      }));
      updatePollVotes(pollId, optionIndex);
    }
  };

  const updatePollVotes = (pollId, optionIndex) => {
    setPolls(prev => prev.map(poll => {
      if (poll.id === pollId) {
        const newVotes = [...poll.votes];
        newVotes[optionIndex]++;
        return { ...poll, votes: newVotes };
      }
      return poll;
    }));
  };

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={6}>
        <Heading size="lg">Active Polls</Heading>
        <Button colorScheme="blue" onClick={onOpen}>
          Open Chat
        </Button>
      </Flex>

      <Grid templateColumns="repeat(auto-fill, minmax(300px, 1fr))" gap={6}>
        {polls.map((poll) => (
          <Card key={poll.id}>
            <CardBody>
              <Stack spacing={4}>
                <Heading size="md">{poll.question}</Heading>
                <RadioGroup>
                  <Stack>
                    {poll.options.map((option, index) => (
                      <Radio
                        key={index}
                        value={index.toString()}
                        onChange={() => submitVote(poll.id, index)}
                      >
                        {option} ({poll.votes[index]} votes)
                      </Radio>
                    ))}
                  </Stack>
                </RadioGroup>
              </Stack>
            </CardBody>
          </Card>
        ))}
      </Grid>

      <Drawer isOpen={isOpen} onClose={onClose} placement="right">
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>Chat</DrawerHeader>
          <DrawerBody>
            <VStack spacing={4} height="100%">
              <Box flex={1} w="100%" overflowY="auto">
                {messages.map((msg, index) => (
                  <Box key={index} mb={2} p={2} bg="gray.100" borderRadius="md">
                    <Text>{msg.text}</Text>
                    <Text fontSize="xs" color="gray.500">
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </Text>
                  </Box>
                ))}
              </Box>
              <Box w="100%" pb={4}>
                <Input
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type a message..."
                  onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                />
                <Button mt={2} w="100%" onClick={sendMessage}>
                  Send
                </Button>
              </Box>
            </VStack>
          </DrawerBody>
        </DrawerContent>
      </Drawer>
    </Box>
  );
}

export default Home;
